import path from 'path';
import { fileURLToPath } from 'url';

/**
 * Determine the path to a file relative to a module.
 * @param moduleUrl the module URL (use `import.meta.url`)
 * @param relativeFilePath the path to the file, relative to the module
 * @returns {string} the absoluate path of the file
 */
export function pathToFile(moduleUrl, relativeFilePath) {
	const modulePath = fileURLToPath(moduleUrl);
	return path.resolve(path.dirname(modulePath), relativeFilePath);
}
